import React from "react";
import {
  FlatList,
  StyleSheet,
  Text,
  View,
  Image,
  Dimensions,
  ImageBackground,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

import { fetchAllCourses, getAllCourses } from "../Redux/Actions/coursesAction";
import store from "../Redux/Stores/Store";
import { useEffect } from "react";
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import COLORS from '../src/const/colors';

const ListItemView = ({ navigation }) => {
  const db = useSelector((store) => store.courses);
  const dispatch = useDispatch();
  console.log(db);
  const [data, setData] = useState([]);
  useEffect(() => {
    dispatch(fetchAllCourses());
    // setData(db.courses)
    console.log(data);
  }, []);
  const HeaderComponent = () => {
    return (
      <View style={styles.headerListComponent}>
        <Text style={styles.headerComponetTitle}>Menu</Text>
        
        <View style={styles.searchInputContainer}>
            <Icon name="magnify" size={24} color={COLORS.grey} />
            <TextInput
              placeholderTextColor={COLORS.grey}
              placeholder="Search pet to adopt"
              style={{flex: 1}}
            />
            <Icon name="sort-ascending" size={24} color={COLORS.grey} />
          </View>
          <TouchableOpacity
          style={styles.btnAdd}
          onPress={() => navigation.navigate("Create")}
        >
          <Text
            style={{
              justifyContent: "center",
              alignSelf: "center",
              fontSize: 20,
            }}
          >
            Thêm sản phẩm
          </Text>
        </TouchableOpacity>
      </View>
    );
  };
  const ItemComponent = ({ item }) => {
    return (
      <TouchableOpacity
        key={item.docId}
        style={styles.ItemComponent}
        onPress={() => {
          navigation.navigate("Edit", {
            // productCId: item.CateID,
            productId: item.docId,
            productImg: item.img,
            productName: item.name,
            productPrice: item.price,
          });
        }}
      >
        <View style={styles.Iteminfo}>
          <Image source={{ uri: item.img }} style={styles.imgItem} />
        </View>
        <View style={styles.ItemDetail}>
          <Text style={styles.nameTxt}>{item.name}</Text>
          <Text style={{ color: "black" }}>Giá: {item.price} VND</Text>
        </View>
      </TouchableOpacity>
    );
  };
  return (
    <View style={styles.ListItemContainer}>
       <ImageBackground
        source={{
          uri: "https://img.freepik.com/free-vector/hand-painted-watercolor-pastel-sky-background_23-2148902771.jpg?w=2000",
        }}
        resizeMode="cover"
        style={styles.bgContainer}
      >
        <FlatList
          numColumns={2}
          data={db.clouths}
          renderItem={ItemComponent}
          ListHeaderComponent={HeaderComponent}
        ></FlatList>

      </ImageBackground>
    </View>
  );
};

export default ListItemView;
const width = Dimensions.get("window").width - 20;
const styles = StyleSheet.create({
  ListItemContainer: {
    flex: 1,
    backgroundColor: "#eeeeee",
  },
  background: {
    flex: 1,
    padding: 20,
  },
  btnAdd: {
    alignSelf: "flex-end",
    width: 40,
    height: 40,
    // borderRadius: 30,
    backgroundColor: "#80deea",
    opacity: 0.8,
  },
  ItemComponent: {
    marginTop: 50,
    width: width / 2,
    padding: 5,

    borderColor: "#000",
    borderWidth: 1,
    shadowColor: "#000",
    shadowOffset: {
      width: 10,
      height: 15,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.5,
    elevation: 5,
    alignItems: "center",
    justifyContent: "center",
  },
  imgItem: {
    width: 120,
    height: 120,
    borderRadius: 20,
  },
  ItemDetail: {
    textAlign: "center",
    width: "70%",
  },
  nameTxt: {
    color: "Black",
    marginTop: 5,
    fontSize: 18,
    flexWrap: "wrap",
  },
  priceTxt: {
    color: "#64b5f6",
    fontSize: 18,
  },
  detailTxt: {
    color: "black",
    fontSize: 15,
  },
  rating: {
    color: "black",
  },
  headerComponetTitle: {
    color: "black",
    fontSize: 50,
    fontWeight: "bold",
    paddingTop: 10,
  },
  headerListComponent: {
    textAlign: "center",
  },

  // Search
  searchInputContainer: {
    height: 50,
    backgroundColor: COLORS.white,
    borderRadius: 7,
    paddingHorizontal: 20,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  // background
  bgContainer: {
    flex: 1,
    width: "100%",
    alignItems: "center",
  },

});
